import logo from './logo.svg';
import React, { Component } from "react"
import { connect } from 'react-redux'
import './App.css';

export class App extends React.Component {
  render() {
    return(
      <div>
        <h1>
          "This is my first Redux App"
        </h1>
        <h3>
          {this.props.message}
        </h3>
        <button onClick={this.props.buttonClick}/>
      </div>
    )
  }
}




// this is used to retrieve the store state
function mapStateToProps(state) {
  return { message: state.message }
}

// this is used to retrieve the actions and dispatch them to the store
const mapDispatchToProps = (dispatch) => {
  return {
    buttonClick: () => {
    console.log("This is a message")
    dispatch({type: "message change"})
    }
  }
}

// Adding mapStateToProps and mapDispatchToProps into connect function
const AppContainer = connect(
  mapStateToProps,
  mapDispatchToProps
)(App);

export default AppContainer;
